mapper.update:
UPDATE imagination_playground.qna_question
SET title = #{title}, content = #{content}, secret = #{secret}, modified_date = NOW()
WHERE id = #{id} AND user_id = #{user_id}

mapper.select:
SELECT * FROM imagination_playground.qna_question

    WHERE completed = #{completed}

ORDER BY created_date
ASC
LIMIT #{page}, #{page_last}

SELECT COUNT(*)

    WHERE completed = #{completed}

FROM imagination_playground.qna_question

SELECT q.email, u.id, u.user_id, u.title, u.content, u.secret, u.completed, u.created_date, u.modified_date FROM imagination_playground.qna_question q LEFT OUTER JOIN imagination_playground.user u ON u.id = q.user_id WHERE email = #{email}
AND completed = #{completed}
ORDER BY created_date ASC

SELECT COUNT(*)
FROM imagination_playground.qna_question q
WHERE email = #{email}

    AND completed = #{completed}

SELECT * FROM imagination_playground.qna_question
WHERE ${key} LIKE(CONCAT("%", ${value}, "%"))

    AND completed = #{completed}

ORDER BY created_date
ASC
LIMIT #{page}, #{page_last}

SELECT q.email, u.id, u.user_id, u.title, u.content, u.secret, u.completed, u.created_date, u.modified_date
         FROM imagination_playground.qna_question q
         LEFT OUTER JOIN imagination_playground.user u
         ON u.id = q.user_id
WHERE ${key} LIKE(CONCAT("%", ${value}, "%")) AND email = #{email}

    AND completed = #{completed}

ORDER BY created_date
ASC
LIMIT #{page}, #{page_last}

SELECT COUNT(*)
FROM imagination_playground.qna_question q
WHERE ${key} LIKE(CONCAT("%", ${value}, "%")) AND email = #{email}

    AND completed = #{completed}
