mapper.select:
SELECT email, id
FROM imagination_playground.user
WHERE email = #{email}

mapper.update:
UPDATE imagination_playground.user
SET username = #{username}, document = #{document}, modified_date = NOW()
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM imagination_playground.user
WHERE email = #{email}

SELECT COUNT(*)
FROM imagination_playground.user
WHERE email = #{email}

SELECT COUNT(*)
FROM imagination_playground.user_authentication
WHERE email = CONCAT('"',#{email}, '"') AND code = #{CODE}
