mapper.insert:
UPDATE imagination_playground.password
SET password = #{password}
WHERE id = #{id}

mapper.update:
UPDATE imagination_playground.user
SET username = #{username}, modified_date = NOW()
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM imagination_playground.user
WHERE email = #{email}

mapper.insert:
UPDATE imagination_playground.user
SET document = #{document}
WHERE email = #{email}

mapper.select:
SELECT COUNT(*)
FROM imagination_playground.user_authentication
WHERE email = CONCAT('"',#{email}, '"') AND code = #{CODE}
